input.new_string:
@layer base {
  * {
    @apply border-border outline-ring/50;
  }
  body {
    @apply bg-background text-foreground;
    font-feature-settings: "cv02", "cv03", "cv04", "cv11";
  }
}

/* Custom Utilities */
@layer utilities {
  .gradient-primary {
    background: var(--gradient-primary);
  }
  .gradient-streak {
    background: var(--gradient-streak);
  }
  .gradient-success {
    background: var(--gradient-success);
  }
  .gradient-card {
    background: var(--gradient-card);
  }
  .shadow-card {
    box-shadow: var(--shadow-card);
  }
  .shadow-glow {
    box-shadow: var(--shadow-glow);
  }
  .shadow-streak {
    box-shadow: var(--shadow-streak);
  }
  .text-streak {
    color: var(--streak);
  }
  .bg-streak {
    background-color: var(--streak);
  }
  .text-success {
    color: var(--success);
  }
  .bg-success {
    background-color: var(--success);
  }
  .bg-habit-completed {
    background-color: var(--habit-completed);
  }
  .bg-habit-pending {
    background-color: var(--habit-pending);
  }
}

/* Animations */
@keyframes check-bounce {
  0%, 100% { transform: scale(1); }
  50% { transform: scale(1.2); }
}

@keyframes flame-flicker {
  0%, 100% { transform: scale(1) rotate(0deg); }
  25% { transform: scale(1.05) rotate(-2deg); }
  50% { transform: scale(0.98) rotate(1deg); }
  75% { transform: scale(1.03) rotate(-1deg); }
}

@keyframes slide-up {
  from { opacity: 0; transform: translateY(10px); }
  to { opacity: 1; transform: translateY(0); }
}

@keyframes progress-fill {
  from { transform: scaleX(0); }
  to { transform: scaleX(1); }
}

.animate-check-bounce {
  animation: check-bounce 0.3s ease-out;
}

.animate-flame {
  animation: flame-flicker 1.5s ease-in-out infinite;
}

.animate-slide-up {
  animation: slide-up 0.4s ease-out;
}

.animate-progress {
  animation: progress-fill 0.6s ease-out;
  transform-origin: left;
}

